package gettingbacktocode;

/**
 * @author evanb
 *
 */
public class WorkingWithInts {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//working with integers
		
		//declare
		int i = 2;
		int z = 4;
		int y = 5;
		
		//add
		System.out.println(i + z + y); //use addition (+) signs
		
		//multiply
		System.out.println(i * z * y); //use multiplication (*) signs
		
		//divide
		System.out.println(i / z / y); //use division (/) signs
		
		//subtract
		System.out.println(i - z - y);
		
		//notice the last line. It says 0. We can fix this by declaring the ints as floats
		float u = 2;
		float x = 4;
		float t = 6;
		
		//divide again;
		System.out.println(u / x / t);

	}

}
